public class Tarea1_10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int bdyear = 1989;
		numeroDiez(bdyear);

	}
	
	public static void numeroDiez (int bdyear) {
		int edad = 2019 - bdyear;
		System.out.println("Si naciste en "+bdyear+" este año tendrás "+edad+" años.");
	}

}
